  {
    "name": "Tostadas de Aguacate",
    "author": "Clara Rosales",
    "image_link": "https://i0.wp.com/passionfood.ec/wp-content/uploads/2019/12/TOSTADA-AGUACATE.jpg",
    "recipe": "Tuesta una rebanada de pan integral, unta aguacate maduro y espolvorea con sal, limón y chile en hojuelas. Ideal para un desayuno rápido."
  },
  {
    "name": "Huevos a la Mexicana",
    "author": "Carlos Jiménez",
    "image_link": "https://conave.org/wp-content/uploads/2020/03/huevos-mexicana.jpg",
    "recipe": "Sofríe tomate, cebolla y chile jalapeño, añade los huevos y revuelve hasta cocer. Sirve con tortillas calientes."
  },
  {
    "name": "Pasta con Pesto",
    "author": "Lucía Ferrer",
    "image_link": "https://www.lavanguardia.com/files/og_thumbnail/uploads/2020/05/29/5ed11fb61d750.jpeg",
    "recipe": "Cocina pasta al dente y mézclala con pesto de albahaca, piñones, ajo, queso parmesano y aceite de oliva."
  },
  {
    "name": "Sopa de Tomate",
    "author": "Gabriel Soto",
    "image_link": "https://www.recetasnestle.com.ar/sites/default/files/srh_recipes/41d0d43e53b03fc14ebcfde36833ad93.jpg",
    "recipe": "Cuece tomates, cebolla y ajo; licúa y cuela. Cocina con albahaca y un toque de crema. Sírvela caliente."
  },
  {
    "name": "Sandwich de Pollo",
    "author": "Paula Méndez",
    "image_link": "https://www.recetasnestlecam.com/sites/default/files/srh_recipes/c5ad0cfe9d4beb9d633c9709113a1452.jpg",
    "recipe": "Rellena pan integral con pollo desmenuzado, lechuga, tomate y mayonesa. Ideal para almuerzos rápidos."
  },
  {
    "name": "Arroz con Verduras",
    "author": "Mateo Vargas",
    "image_link": "https://example.com/images/arroz-verduras.jpg",
    "recipe": "Sofríe zanahoria, arvejas y pimentón; agrega arroz cocido y saltea unos minutos. Acompaña con salsa soya."
  },
  {
    "name": "Panqueques de Avena",
    "author": "Valeria Núñez",
    "image_link": "https://example.com/images/panqueques-avena.jpg",
    "recipe": "Mezcla avena, huevo, plátano y canela; cocina en sartén antiadherente. Sirve con miel o frutas."
  },
  {
    "name": "Ensalada Mediterránea",
    "author": "Antonio Salas",
    "image_link": "https://example.com/images/ensalada-mediterranea.jpg",
    "recipe": "Combina pepino, tomate, aceitunas negras, cebolla morada y queso feta. Adereza con aceite de oliva y orégano."
  },
  {
    "name": "Tortilla Española",
    "author": "Laura Pérez",
    "image_link": "https://example.com/images/tortilla-espanola.jpg",
    "recipe": "Fríe papas y cebolla en aceite de oliva, mezcla con huevo batido y cocina a fuego medio hasta dorar."
  },
  {
    "name": "Batido de Fresas",
    "author": "Andrés Morales",
    "image_link": "https://example.com/images/batido-fresas.jpg",
    "recipe": "Licúa fresas, leche, yogurt y miel. Un batido refrescante y nutritivo para cualquier hora del día."
  },
  {
    "name": "Croquetas de Papa",
    "author": "Renata Blanco",
    "image_link": "https://example.com/images/croquetas-papa.jpg",
    "recipe": "Haz puré de papa, mezcla con queso, forma bolitas y fríelas hasta dorar. Perfectas como aperitivo."
  },
  {
    "name": "Tacos de Pescado",
    "author": "Julián Herrera",
    "image_link": "https://example.com/images/tacos-pescado.jpg",
    "recipe": "Fríe filetes de pescado empanizado, coloca en tortillas y acompaña con repollo, crema y limón."
  },
  {
    "name": "Pizza Rápida en Sartén",
    "author": "Camila Ruiz",
    "image_link": "https://www.recetasnestle.com.mx/sites/default/files/srh_recipes/6a1a8f28fdbeab6a670bd8be9daac7fd.jpg",
    "recipe": "Usa una tortilla de harina como base, añade salsa, queso y tus ingredientes favoritos. Cocina tapada en sartén."
  },
  {
    "name": "Galletas de Banano",
    "author": "Tomás Castillo",
    "image_link": "https://example.com/images/galletas-banano.jpg",
    "recipe": "Mezcla puré de banano, avena y chips de chocolate. Hornea 15 minutos a 180°C. ¡Sin azúcar añadida!"
  },
  {
    "name": "Quesadillas de Queso",
    "author": "Sofía Martínez",
    "image_link": "https://example.com/images/quesadillas-queso.jpg",
    "recipe": "Coloca queso entre dos tortillas y calienta en sartén hasta derretir. Sírvelas con salsa o guacamole."
  }
]
}
